/* changing switcher on kde muzu dluzit page + showing / hiding content - runs only at page KDE MUZU DLUZIT */
jQuery(function($){
	if (jQuery("body").hasClass("page-id-3511")) {
    	/* adding ACTIVE class to option by default */
        $("#prepinac-dotaznik .wpcf7-list-item.first .wpcf7-list-item-label").addClass('active');
        /* changing ACTIVE class to 2nd option when is clicked */
        $("#prepinac-dotaznik .wpcf7-list-item.last .wpcf7-list-item-label").click(function(){
            $("#prepinac-dotaznik .wpcf7-list-item.first .wpcf7-list-item-label").removeClass('active');
            $("#prepinac-dotaznik .wpcf7-list-item.last .wpcf7-list-item-label").addClass('active');
            $("#dotaznik-sekce").addClass('d-none'); 
            $("#instituce-sekce").removeClass('d-none').addClass('d-block');         
            /* changing ACTIVE class back to 1st option when is clicked */
            $("#prepinac-dotaznik .wpcf7-list-item.first .wpcf7-list-item-label").click(function(){
                $("#prepinac-dotaznik .wpcf7-list-item.last .wpcf7-list-item-label").removeClass('active');
                $("#prepinac-dotaznik .wpcf7-list-item.first .wpcf7-list-item-label").addClass('active');   
                $("#instituce-sekce").addClass('d-none'); 
                $("#dotaznik-sekce").removeClass('d-none').addClass('d-block');      
            });
        });
	}
});   

/* UX customization of blurb - poradny with icon for mobile devices - RUNS only on page PORADNY*/
jQuery(function($) {
  	if ((jQuery("body").hasClass("page-id-3882")) & (jQuery(window).width() <= 767)) {
      	jQuery(".dluhova-poradna").each(function (index) {
          	jQuery(this).find(".et_pb_main_blurb_image").prependTo(jQuery(this).find(".et_pb_blurb_container"));
       	});
    }
});

